import React from 'react';
import { Typography, Paper } from '@mui/material';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const SpendingCategories = ({ transactions }) => {
  const totalIncome = transactions
    .filter(transaction => transaction.category === 'Income')
    .reduce((sum, transaction) => sum + transaction.amount, 0);

  const totalExpenses = transactions
    .filter(transaction => transaction.amount < 0)
    .reduce((sum, transaction) => sum + (-transaction.amount), 0);

  const salaryLeft = totalIncome - totalExpenses;

  const categoryData = transactions.reduce((categories, transaction) => {
    if (transaction.category !== 'Income') {
      categories[transaction.category] = (categories[transaction.category] || 0) + (-transaction.amount);
    }
    return categories;
  }, {});

  if (Object.keys(categoryData).length === 0) {
    categoryData['Salary Left'] = salaryLeft;
  }

  const data = {
    labels: [...Object.keys(categoryData), ...(salaryLeft > 0 ? ['Salary Left'] : [])],
    datasets: [
      {
        data: [...Object.values(categoryData), ...(salaryLeft > 0 ? [salaryLeft] : [])],
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4CAF50'],
        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4CAF50'],
      },
    ],
  };

  return (
    <Paper elevation={3} style={{ padding: '20px', marginBottom: '20px' }}>
      <Typography variant="h6" gutterBottom>
        Spending Categories
      </Typography>
      <Pie data={data} />
    </Paper>
  );
};

export default SpendingCategories;
