import React from 'react';
import { Typography, Paper, LinearProgress, Box } from '@mui/material';

const BudgetTracking = ({ transactions }) => {
  const totalBudget = 1000;
  const totalSpent = transactions.reduce((sum, transaction) => sum + (transaction.amount < 0 ? -transaction.amount : 0), 0);
  const progress = (totalSpent / totalBudget) * 100;

  return (
    <Paper elevation={3} style={{ padding: '20px', marginBottom: '20px' }}>
      <Typography variant="h6" gutterBottom>
        Budget Tracking
      </Typography>
      <Box display="flex" justifyContent="space-between">
        <Typography variant="body2">Budget: ${totalBudget}</Typography>
        <Typography variant="body2">Spent: ${totalSpent}</Typography>
      </Box>
      <LinearProgress variant="determinate" value={progress} style={{ marginTop: '10px', height: '10px', borderRadius: '5px' }} />
      <Box display="flex" justifyContent="space-between" style={{ marginTop: '10px' }}>
        <Typography variant="body2" color="textSecondary">0%</Typography>
        <Typography variant="body2" color="textSecondary">{progress.toFixed(2)}%</Typography>
        <Typography variant="body2" color="textSecondary">100%</Typography>
      </Box>
    </Paper>
  );
};

export default BudgetTracking;
