import { Box, Typography, TextField, Button, Container, Paper } from "@mui/material";
import { useState } from "react";
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { useAuth } from '../context/AuthContext'; // Import useAuth from AuthContext

const theme = createTheme({
  palette: {
    primary: {
      main: '#0D47A1',  // Deep blue, adjust according to Spend Ruby's exact color scheme
    },
    secondary: {
      main: '#FFFFFF',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    button: {
      textTransform: 'none',  // If you prefer button text to be normally cased
    },
  },
});

const Welcome = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  const handleSubmission = async () => {
    // Placeholder for submission logic
    console.log({ username, password, confirmPassword });
  };

  const { login } = useAuth(); // Use useAuth hook here

  const handleBypassLogin = () => {
    const handleBypassLogin = () => {
      login();
    };

    return (
      <Container component="main" maxWidth="xs">
        <Paper elevation={6} style={{ marginTop: '20vh', padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Typography component="h1" variant="h5" style={{ margin: '20px 0' }}>Login</Typography>
          <TextField label="Username" variant="outlined" fullWidth margin="normal" />
          <TextField label="Password" variant="outlined" type="password" fullWidth margin="normal" />
          <Button variant="contained" color="primary" fullWidth style={{ margin: '20px 0' }} onClick={handleBypassLogin}>Bypass Login</Button>
        </Paper>
      </Container>
    );
  };

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <Paper elevation={6} style={{ marginTop: '20vh', padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <Typography component="h1" variant="h5" style={{ margin: '20px 0' }}>Sign Up</Typography>
          <TextField
            margin="normal"
            required
            fullWidth
            id="username"
            label="Username"
            name="username"
            autoComplete="username"
            autoFocus
            value={username}
            onChange={e => setUsername(e.target.value)}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="password"
            label="Password"
            type="password"
            id="password"
            autoComplete="current-password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
          <TextField
            margin="normal"
            required
            fullWidth
            name="confirmPassword"
            label="Confirm Password"
            type="password"
            id="confirmPassword"
            value={confirmPassword}
            onChange={e => setConfirmPassword(e.target.value)}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
            style={{ margin: '20px 0' }}
            onClick={handleSubmission}
          >
            Sign Up
          </Button>
        </Paper>
      </Container>
    </ThemeProvider>
  );
};

export default Welcome;
