import React from 'react';
import { Typography, Paper, LinearProgress } from '@mui/material';

const BudgetTracking = ({ budget, transactions }) => {
  if (!budget) return null;

  const totalSpent = transactions.reduce((acc, transaction) => acc + transaction.amount, 0);
  const progress = (totalSpent / budget.amount) * 100;

  return (
    <Paper elevation={3} style={{ padding: '20px' }}>
      <Typography variant="h6" gutterBottom>
        Budget Tracking
      </Typography>
      <Typography variant="body1">Budget: ${budget.amount}</Typography>
      <Typography variant="body1">Spent: ${totalSpent}</Typography>
      <LinearProgress variant="determinate" value={progress} style={{ marginTop: '10px' }} />
    </Paper>
  );
};

export default BudgetTracking;
